        [NUnit.Framework.TestCaseSource("ProvideCountryData")]
        public virtual void TestDifferentCountries(CountrySpecificLotl countrySpecificLotl, String certificate) {
            // TODO DEVSIX-9626: To create an investigation ticket
            if ("MX".Equals(countrySpecificLotl.GetSchemeTerritory()) || "RS".Equals(countrySpecificLotl.GetSchemeTerritory
                ())) {
                return;
            }
            System.Console.Out.WriteLine("Testing country: " + countrySpecificLotl.GetSchemeTerritory() + " with TSL: "
                 + countrySpecificLotl.GetTslLocation());
            LotlFetchingProperties props = new LotlFetchingProperties(new RemoveOnFailingCountryData());
            LotlService lotlService = new SingleFileLotlService(props, countrySpecificLotl, JavaCollectionsUtil.SingletonList
                (certificate));
            lotlService.WithCustomResourceRetriever(new FromDiskResourceRetriever(SOURCE_FOLDER));
            LotlValidator validator = new LotlValidator(lotlService);
            lotlService.WithLotlValidator(() => validator);
            ValidatorChainBuilder chainBuilder = new ValidatorChainBuilder().WithLotlService(() => lotlService).TrustEuropeanLotl
                (true);
            lotlService.InitializeCache();
            LotlTrustedStore lotlTrustedStore = chainBuilder.GetLotlTrustedStore();
            ICollection<IX509Certificate> certificates = lotlTrustedStore.GetCertificates();
            System.Console.Out.WriteLine("total Certificates for " + countrySpecificLotl.GetTslLocation() + " : " + certificates
                .Count);
            ValidationReport report = lotlTrustedStore.GetLotlValidationReport();
            System.Console.Out.WriteLine(report);
            NUnit.Framework.Assert.IsTrue(report.GetFailures().IsEmpty());
        }

        [NUnit.Framework.Test]
        [LogMessage(SignLogMessageConstant.SCHEMA_NAMES_CONFIGURATION_PROPERTY_IGNORED, LogLevel = LogLevelConstants
            .WARN)]
        public virtual void CountryNamesIgnoredTest() {
            LotlFetchingProperties props = new LotlFetchingProperties(new RemoveOnFailingCountryData());
            props.SetCountryNames("AR");
            NUnit.Framework.Assert.DoesNotThrow(() => {
                LotlService lotlService = new SingleFileLotlService(props, new CountrySpecificLotl("AR", "https://pki.jgm.gov.ar/TSL/tsl-AR.xml"
                    , "application/vnd.etsi.tsl+xml"), JavaCollectionsUtil.SingletonList(argentinaCertificate));
            }
            );
        }

